mapper.update:
update myapp_board set
  meal=#{meal},
  diet=#{diet}
where 
  board_no=#{no}
  and writer=#{writer.no}
  and diet=#{diet.no}

update myapp_board set
  view_count=#{viewCount}
where
  board_no=#{no}

mapper.delete:
delete from myapp_board
where 
  board_no=#{no}
  and writer=#{writer.no}
  and diet=#{diet.no}
